const array1 = [1, 2, 3];

console.log(array1.unshift(4, 5));
// 5 (dizinin yeni uzunluğu)

console.log(array1);
// [4, 5, 1, 2, 3]

examples.code:
let arr = [1, 2]

arr.unshift(0); //  geriye 3 döndürür (dizi uzunluğu)
console.log(arr);
// [0, 1, 2]

arr.unshift(-2, -1); // geriye 5 döndürür (dizi uzunluğu)
console.log(arr);
// [-2, -1, 0, 1, 2]

arr.unshift([-4, -3]); // geriye 6 döndürür (dizi uzunluğu)
console.log(arr);
// [[-4, -3], -2, -1, 0, 1, 2]

arr.unshift([-7, -6], [-5]); // geriye 8 döndürür (dizi uzunluğu)
console.log(arr);
// [ [-7, -6], [-5], [-4, -3], -2, -1, 0, 1, 2 ]
